const DungeonScreen = () => {
  const [currentScreen, setCurrentScreen] = useState('newsList');

  const [selectedNews, setSelectedNews] = useState(null);

  const [answeredQuestion, setAnsweredQuestion] = useState(null);

  const resetSelectedNews = () => {
    setAnsweredQuestion(selectedNews.id);
    setSelectedNews(null);
    setCurrentScreen('newsList');
  };

  const backToNewsList = () =>{
    setCurrentScreen('newsList');
  }

  const handleNewsSelect = (newsItem) => {
    setSelectedNews(newsItem);
    setCurrentScreen('newsDetail');
  }

  const renderScreen = () => {
    switch (currentScreen) {
      case 'newsList':
        return <NewsList
          newsData={newsData}
          onSelect={handleNewsSelect}
          answeredQuestion={answeredQuestion} />;
      case 'newsDetail':
        return <NewsDetail
          news={selectedNews}
          onNext={() => setCurrentScreen('quiz')} 
          onGoToNewsList={() => backToNewsList()}
          />;
      case 'quiz':
        return <Quiz
          quizData={selectedNews}
          onGoToNewsDetail={() => setCurrentScreen('newsDetail')}
          onGoToNewsList={() => resetSelectedNews()}
        />;
      default:
        return <NewsList newsData={newsData} onSelect={handleNewsSelect} />;
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.content}>{renderScreen()}</View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  content: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    width: '100%',
  },
});

export default DungeonScreen;
